mapper.select:
SELECT c.`name` as communityname,c.id as communityid ,b.`name` as buildingname,b.id as buildingid ,h.houseId ,h.`unitNumber`,h.`fool`,t.`id`,t.`name` as tenementname FROM tenement t INNER JOIN house_tentment ht ON ht.`tentmentId`=t.`id` INNER JOIN house h ON h.`houseId`=ht.`houseId` INNER JOIN house_buliding hb ON h.`houseId`=hb.`houseId` INNER JOIN building b ON b.`id`=hb.`buildingId` INNER JOIN community c ON c.`id`=t.`communityId` WHERE 1=1
and t.name LIKE CONCAT ('%',#{tenementName},'%')
limit #{start},#{limit}

SELECT tc.`carportId` FROM tenement t
INNER JOIN tenement_carport tc ON tc.`tentmentId`=t.`id`
WHERE phone=#{phone}

SELECT ht.`houseId` FROM tenement t
INNER JOIN house_tentment ht ON t.`id`=ht.tentmentId
WHERE phone=#{phone}
