mapper.select:
SELECT bi.*,cid.type,comm.id as id1,comm.`name` as communityName,cid.signId,cna.`name` as charName,te.`name` as tentmentName,te.phone,cit.price FROM bill bi INNER JOIN charge_id cid ON bi.id=cid.billId INNER JOIN community comm ON comm.id=bi.communityId INNER JOIN house_buliding hb ON cid.signId=hb.houseId INNER JOIN building bu on bu.id=hb.buildingId INNER JOIN chargeitem cit ON cit.id=cid.chargeitemId INNER JOIN chargename cna ON cna.id=cit.type INNER JOIN house_tentment ht ON ht.houseId=cid.signId INNER JOIN house ON house.houseId=ht.houseId INNER JOIN tenement te on te.id=ht.tentmentId INNER JOIN carport cp ON cp.carportNo=cid.type WHERE bi.state=27
AND comm.`name` LIKE CONCAT ('%',#{name},'%') OR bu.name LIKE CONCAT ('%',#{name},'%') OR cp.carportNo LIKE CONCAT ('%',#{name},'%') OR house.houseId LIKE CONCAT ('%',#{name},'%')
limit #{page},#{pageSize}

SELECT bi.*,cid.type,comm.id as id1,comm.`name` as communityName,cid.signId,cna.`name` as charName,te.`name` as tentmentName,te.phone,cit.price,ad.id AS adminid,ad.name as adminName FROM bill bi INNER JOIN charge_id cid ON bi.id=cid.billId INNER JOIN community comm ON comm.id=bi.communityId INNER JOIN house_buliding hb ON cid.signId=hb.houseId INNER JOIN building bu on bu.id=hb.buildingId INNER JOIN chargeitem cit ON cit.id=cid.chargeitemId INNER JOIN chargename cna ON cna.id=cit.type INNER JOIN house_tentment ht ON ht.houseId=cid.signId INNER JOIN house ON house.houseId=ht.houseId INNER JOIN tenement te on te.id=ht.tentmentId INNER JOIN admin ad on ad.id=bi.chargeUser INNER JOIN carport cp ON cp.carportNo=cid.type WHERE bi.state=28
AND comm.`name` LIKE CONCAT ('%',#{name},'%') OR bu.name LIKE CONCAT ('%',#{name},'%') OR cp.carportNo LIKE CONCAT ('%',#{name},'%') OR house.houseId LIKE CONCAT ('%',#{name},'%')
limit #{page},#{pageSize}
